use Zend\Http\Client;\n\nfunction getWikipediaData($pageTitle) {\n    $client = new Client();\n    $client->setUri('https://en.wikipedia.org/w/api.php');\n    \n    // Set up parameters for fetching page content\n    $params = [\n        'action' => 'query',\n        'prop' => 'extracts|info', // Get extracts and information about the page\n        'explaintext' => '', // Return extract without HTML tags\n        'titles' => $pageTitle,\n        'format' => 'json',\n        'inprop' => 'url',\n    ];\n\n    $client->setParameterGet($params);\n    $response = $client->send();\n    \n    if ($response->isSuccess()) {\n        $data = json_decode($response->getBody(), true);\n        \n        // Extract page data\n        if (isset($data['query']['pages'])) {\n            foreach ($data['query']['pages'] as $page) {\n                return [\n                    'title' => isset($page['title']) ? $page['title'] : null,\n                    'content' => isset($page['extract']) ? $page['extract'] : null,\n                    'url' => isset($page['fullurl']) ? $page['fullurl'] : null\n                ];\n            }\n        } else {\n            return ['error' => 'Page not found or no data returned'];\n        }\n    } else {\n        return ['error' => 'Failed to fetch data from Wikipedia'];\n    }\n}\n\n// Example usage\n$pageInfo = getWikipediaData('Stade de France');\nprint_r($pageInfo);