for(/*extracting from the translated string array*/) {\n    $pattern = "/msgid \"" . preg_quote($id, '/') . "\"(\r?\n)msgstr \"\"/";\n    // Ensure that the input string is treated as UTF-8\n    $string = mb_convert_encoding($string, 'UTF-8', 'auto');\n    $string = str_replace('"', '\"', $string);\n    $replacement = "msgid \"$id\"\nmsgstr \"" . $string . "\"";\n    $res = preg_replace($pattern, $replacement, $con);\n    $con = $res;\n}\n// Set the file to be written in UTF-8\nfile_put_contents("ja.po", "\xEF\xBB\xBF" . $con); // Adding BOM for UTF-8