<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Assuming venues are submitted in an array format\n    if (!empty($_POST['venues'])) {\n        $assignments = $_POST['venues'];\n        \n        // Validate and sanitize inputs to prevent injection attacks\n        foreach ($assignments as $volunteerId => $venue) {\n            $sanitizedVenue = filter_input(INPUT_POST, "venues[$volunteerId]", FILTER_SANITIZE_NUMBER_INT);\n            \n            // Here you would typically insert/update the database with $volunteerId and $sanitizedVenue\n            echo "Assigning Volunteer ID $volunteerId to Venue $sanitizedVenue<br>";\n        }\n    }\n}\n?>