<?php\n$connection = ssh2_connect('shell.example.com', 22);\nif (!$connection) die('Connection failed.');\n\n$auth = ssh2_auth_password($connection, 'jdoe', 'password');\nif (!$auth) die('Authentication failed.');\n\n// Upload the file\nssh2_scp_send($connection, '/local/filename.txt', '/remote/filename.txt');\n\n// Change the owner and group of the file\n$command = "sudo chown newuser:newgroup /remote/filename.txt";\n$stream = ssh2_exec($connection, $command);\n\nif (!$stream) die('Command execution failed.');\n\nstream_set_blocking($stream, true);\n$output = stream_get_contents($stream);\nfclose($stream);\n\necho $output;\n?>