<?php\n\n$defines = [\n    "DEFINE('TEXT', 'VALUE');",\n    "DEFINE('ANOTHER_TEXT', 'J\\'ai');"\n];\n\n$result = [];\n\nforeach ($defines as $define) {\n    preg_match('/DEFINE\(\'([^']*)\', \'([^\'\\\\]*(?:\\\\.[^\'\\\\]*)*)\'\);/', $define, $matches);\n    if (count($matches) == 3) { // Ensure we have a correct match\n        $result[$matches[1]] = stripcslashes($matches[2]);\n    }\n}\n\nprint_r($result);\n\n?>