<?php\n\nclass Vehicle {\n    protected $model;\n    private $year;\n\n    public function __construct($model, $year) {\n        $this->model = $model;\n        $this->year = $year;\n    }\n\n    public function getInfo() {\n        return "This is a {$this->year} {$this->model}.";\n    }\n}\n\nclass Car extends Vehicle {\n    private $engineType;\n\n    public function __construct($model, $year, $engineType) {\n        parent::__construct($model, $year);\n        $this->engineType = $engineType;\n    }\n\n    public function getDescription() {\n        return "This car is a {$this->getModel()} with a {$this->engineType} engine.";\n    }\n}\n\n$myCar = new Car("Toyota Camry", 2015, "V6");\necho $myCar->getInfo(); // Output: This is a 2015 Toyota Camry.\necho "\n";\necho $myCar->getDescription(); // Output: This car is a Toyota Camry with a V6 engine.\n\n?>