private static function sortByDistance($a, $b) {\n    if (!is_numeric($a['distance']) || !is_numeric($b['distance'])) {\n        // Handle non-numeric case, e.g., treat as 0 or throw an error.\n        return 0;\n    }\n    \n    if ($a['distance'] > $b['distance']) {\n        return 1;\n    } elseif ($a['distance'] < $b['distance']) {\n        return -1;\n    }\n    // If distances are equal, maintain original order\n    return 0;\n}