<?php\n\n// Your input string with UTF-16 hex encoding mixed with ASCII\n$input = "R\00f6dhakev\00e4gen 4\nR\00e4ntm\00e4starv\00e4gen 24\nK\00d8BENHAVN";\n\n// Remove backslashes and convert to a proper binary string\n$hexString = str_replace('\\', '', $input);\n\n// Convert the hex string to a binary UTF-16 string\n$binaryUtf16 = pack('H*', $hexString);\n\n// Decode from UTF-16 to UTF-8\n$utf8String = mb_convert_encoding($binaryUtf16, 'UTF-8', 'UTF-16');\n\necho $utf8String;\n\n?>