function getRootDomain($domain) {\n    // Match the pattern to extract the root domain with a 3-letter TLD\n    if (preg_match('/(?P<root>[a-z0-9-]+\.[a-z]{3})$/', $domain, $matches)) {\n        return $matches['root'];\n    }\n    return null; // Return null if no match is found\n}\n\n// Example usage:\necho getRootDomain("here.example.com"); // Outputs: example.com\necho "\n";\necho getRootDomain("example.com");      // Outputs: example.com\necho "\n";\necho getRootDomain("example.org");      // Outputs: example.org\necho "\n";\necho getRootDomain("here.example.org"); // Outputs: example.org