<?php\nrequire_once __DIR__ . '/vendor/autoload.php';\n\n$fb = new \Facebook\Facebook([\n  'app_id' => '{your-app-id}',\n  'app_secret' => '{your-app-secret}',\n  'default_graph_version' => 'v15.0',\n]);\n\n$accessToken = '{valid-access-token}'; // Obtain user access token with appropriate permissions\n\ntry {\n  $response = $fb->post('/{user-id}/{custom-action-type}', [\n    '{custom-action-parameters}' => '{values}', // Replace with your action parameters and values\n  ], $accessToken);\n\n  $graphNode = $response->getGraphNode();\n  echo 'Post ID: ' . $graphNode['id'];\n\n} catch(\Facebook\Exceptions\FacebookResponseException $e) {\n  echo 'Graph returned an error: ' . $e->getMessage();\n  exit;\n} catch(\Facebook\Exceptions\FacebookSDKException $e) {\n  echo 'Facebook SDK returned an error: ' . $e->getMessage();\n  exit;\n}\n?>