function convert() {\n    $this->db->open();\n    $sql_select = "SELECT JOBNUMBER, INSTALLDATE FROM ACCOUNTS";\n    $result = $this->db->query($sql_select);\n\n    if (!$result) {\n        die("Error selecting data: " . mysql_error());\n    }\n\n    $str = '';\n    while ($row = $this->db->fetchassoc($result)) {\n        $jobNum = $row['JOBNUMBER'];\n        $old_date = $row['INSTALLDATE'];\n        $new_date = date("Y-m-d", strtotime($old_date));\n        $sql_update = "UPDATE ACCOUNTS SET INSTALLDATE='$new_date' WHERE JOBNUMBER='$jobNum'";\n\n        if (!$this->db->query($sql_update)) {\n            die("Error updating row with JOBNUMBER '$jobNum': " . mysql_error());\n        }\n\n        $str .= "$jobNum -- $new_date<br />";\n    }\n    \n    return $str;\n}