function sqlf($query, $params = []) {\n    if (empty($params)) {\n        // Modify the query to safely handle an empty IN clause\n        return "SELECT * FROM Users WHERE 1=0"; // This will never match any row\n    }\n\n    // Assuming $params is a single array for simplicity\n    $placeholders = [];\n    foreach ($params as $key => $value) {\n        if (is_array($value)) { // Check if it's an array of values for IN clause\n            if (empty($value)) {\n                return "SELECT * FROM Users WHERE 1=0"; // Handle empty array case\n            }\n            $placeholders[] = implode(',', array_fill(0, count($value), '?'));\n        } else {\n            $placeholders[] = '?';\n        }\n    }\n\n    $finalQuery = str_replace([':1'], [$placeholders[0]], $query);\n    return [$finalQuery, ...$params]; // Return the query and params\n}\n\n// Example usage:\n$sql = sqlf("SELECT * FROM Users WHERE id IN (:1)", []);\necho "SQL Query: " . $sql[0] . PHP_EOL;