<?php\n\n$url = "http://www.sportingpulse.com/rpt_ladder.cgi?results=N&round=15&client=1-3909-47801-81021-61514&pool=-1";\n\n// Initialize a cURL session\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the request and get the response\n$response = curl_exec($ch);\n\nif ($response === false) {\n    echo "cURL Error: " . curl_error($ch);\n} else {\n    // Create a new DOMDocument instance\n    $dom = new DOMDocument();\n\n    // Suppress errors due to malformed HTML\n    libxml_use_internal_errors(true);\n\n    // Load the HTML into the DOMDocument object\n    $dom->loadHTML($response);\n\n    // Find all tables in the document\n    $tables = $dom->getElementsByTagName('table');\n\n    if ($tables->length > 0) {\n        // Assuming you want the first table, change index if needed\n        $tableContent = $dom->saveHTML($tables->item(0));\n        echo $tableContent;\n    } else {\n        echo "No tables found.";\n    }\n}\n\n// Close cURL session\ncurl_close($ch);\n\n?>