// Connect to MySQL (make sure to replace with mysqli or PDO for better security)\n$conn = mysql_connect("host", "user", "password");\nmysql_select_db("database_name", $conn);\n\n// Get the current time\n$now = new DateTime();\n\n// Fetch the latest 25 entries ordered by date in descending order\n$result = mysql_query("SELECT `code`, `dates` FROM `fc` ORDER BY `dates` DESC LIMIT 25") or die(mysql_error());\n\n$output = '';\n\nwhile ($fetch_array = mysql_fetch_assoc($result)) {\n    // Convert the database date to a DateTime object\n    $date = new DateTime($fetch_array["dates"]);\n    \n    // Calculate the difference between now and the entry's date\n    $diff = $now->diff($date);\n    \n    // Format the time since\n    if ($diff->s > 0 && $diff->i == 0) {\n        $timeAgo = $diff->s . ' Second' . ($diff->s > 1 ? 's' : '');\n    } elseif ($diff->i > 0 && $diff->h == 0) {\n        $timeAgo = $diff->i . ' Minute' . ($diff->i > 1 ? 's' : '');\n    } else {\n        $timeAgo = $diff->h . ' Hour' . ($diff->h > 1 ? 's' : '');\n    }\n    \n    // Append to output\n    $output .= "<li><a href=\"http://www.***=" . htmlspecialchars(urlencode($fetch_array["code"])) . "\" target=\"_blank\">" \n             . htmlspecialchars($fetch_array["code"]) . " (". $timeAgo ." Ago)</a></li>";\n}\n\necho $output;\n\n// Close the MySQL connection\nmysql_close($conn);