use PDO;\n\n// Ensure the data is handled as a parameter bound to a prepared statement\n$db = Database::instance();\n$query = $db->query(Database::INSERT, '\n    INSERT INTO `attachments` (`name`, `size`, `data`, `mime_type`) \n    VALUES (:name, :size, :data, :mime_type)\n', array(\n    ':name' => $info['FileName'],\n    ':size' => strlen($info['Data']),\n    ':data' => PDO::PARAM_LOB,\n    ':mime_type' => $info['content-type']\n));\n\n// Bind the large object\n$stmt = $query->execute(array(':data' => $info['Data']));