function normalizeArrayDepth($array) {\n    // Function to determine the depth of an array\n    $getDepth = function ($value, $maxDepth = 0) use (&$getDepth) {\n        foreach ((array)$value as $item) {\n            if (is_array($item)) {\n                $maxDepth++;\n                return max($maxDepth, $getDepth($item, $maxDepth));\n            }\n        }\n        return $maxDepth;\n    };\n\n    // Determine current depth of the array\n    $currentDepth = $getDepth($array);\n\n    // Normalize to 3 levels deep if necessary\n    switch ($currentDepth) {\n        case 1:\n            $array = [[$array]];\n            break;\n        case 2:\n            $array = [$array];\n            break;\n    }\n\n    return $array;\n}\n\n// Example usage:\n$array1 = [[['value1', 'value2'], ['value3']], [['value4']]];\n$array2 = [['value1', 'value2'], ['value3']];\n\n$normalizedArray1 = normalizeArrayDepth($array1);\n$normalizedArray2 = normalizeArrayDepth($array2);\n\nprint_r($normalizedArray1);\nprint_r($normalizedArray2);