<?php\n\nfunction rsaToXml($key) {\n    $details = openssl_pkey_get_details(openssl_pkey_get_private($key));\n    $modulus = base64_encode($details['rsa']['n']);\n    $exponent = base64_encode($details['rsa']['e']);\n\n    $xml = new DOMDocument('1.0', 'UTF-8');\n    $root = $xml->createElement("RSAKeyValue");\n    $xml->appendChild($root);\n\n    $modulusElement = $xml->createElement("Modulus", $modulus);\n    $exponentElement = $xml->createElement("Exponent", $exponent);\n    \n    $root->appendChild($modulusElement);\n    $root->appendChild($exponentElement);\n\n    return $xml->saveXML();\n}\n\n$privateKeyXml = rsaToXml('private_key.pem');\necho $privateKeyXml;\n\n?>