<?php\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n    // Ensure that native prepared statements are used\n    $pdo->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);\n    // Optionally set the string fetch attribute to true if you want other values as strings\n    $pdo->setAttribute(PDO::ATTR_STRINGIFY_FETCHES, false);\n\n    // Example query\n    $stmt = $pdo->query('SELECT id, price FROM products');\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        echo "ID: {$row['id']} (Type: " . gettype($row['id']) . ")\n";\n        echo "Price: {$row['price']} (Type: " . gettype($row['price']) . ")\n";\n    }\n} catch (PDOException $e) {\n    die("Could not connect to the database " . $e->getMessage());\n}\n?>