<?php\n$user_id = 5; // Assuming user ID is 5 for the example\n\n// Prepare the SQL query using UNION\n$query = "\n    SELECT 'photo' AS activity_type, photo_id AS item_id, photo_name AS details, date_added FROM photos WHERE user_id = ?\n    UNION ALL\n    SELECT 'video' AS activity_type, video_id AS item_id, '' AS details, date_added FROM videos WHERE user_id = ?\n    UNION ALL\n    SELECT 'comment' AS activity_type, comment_id AS item_id, comment AS details, date_added FROM comments WHERE user_id = ?\n    ORDER BY date_added DESC \n    LIMIT 10;";\n\n// Use prepared statements to avoid SQL injection\n$statement = $pdo->prepare($query);\n$statement->execute([$user_id, $user_id, $user_id]);\n\n// Fetch the results\n$results = $statement->fetchAll();\n\n// Output the results\nforeach ($results as $row) {\n    echo htmlspecialchars($row['date_added']) . ": user posted a " . htmlspecialchars($row['activity_type']);\n    if (!empty($row['details'])) {\n        echo ": " . htmlspecialchars($row['details']);\n    }\n    echo "<br>";\n}\n?>