<?php\n// Get the year, month, and day from the MySQL timestamp\n$dateParts = date("Y, n, j", strtotime($row["date"]));\n\n// Split the date parts into an array\nlist($year, $month, $day) = explode(", ", $dateParts);\n\n// Adjust the month for JavaScript (subtract 1)\n$adjustedMonth = $month - 1;\n\n// Format the date as a string suitable for JavaScript Date\n$jsDate = "$year, $adjustedMonth, $day";\n\n// Use it in your JavaScript code like this:\n?>\ndata.setValue(<?=$count;?>, 0, new Date(<?=$jsDate;?>));