<?php\n$input = '<p>Here is some text <SPAN style="TEXT-DECORATION: underline">text sample</SPAN> and more text.</p>';\n\n// Regular expression pattern to match SPAN tags with the specific style attribute\n$pattern = '/<SPAN style="TEXT-DECORATION: underline">(.*?)<\/SPAN>/i';\n\n// Replacement pattern using the <u> tag\n$replacement = '<u>$1</u>';\n\n$output = preg_replace($pattern, $replacement, $input);\n\necho $output;\n?>