<?php\n\nfunction loadArticles($xmlFile) {\n    $content = file_get_contents($xmlFile);\n    if ($content === false) {\n        die('Error reading XML file');\n    }\n    \n    $xml = simplexml_load_string($content, 'SimpleXMLElement', LIBXML_NOCDATA);\n    \n    if ($xml === false) {\n        die('Error parsing XML');\n    }\n\n    return $xml->article;\n}\n\n$articles = loadArticles('/path/to/your/content.xml');\n\nforeach ($articles as $article) {\n    echo "<h1>" . htmlspecialchars($article->title) . "</h1>";\n    echo "<p><em>Published on: " . htmlspecialchars($article->date) . "</em></p>";\n    echo "<div class=\"content\">" . nl2br(htmlspecialchars($article->body)) . "</div>";\n}\n\n?>