session_start();\n\n   // Check if the token is present and valid\n   $csrf_token = $_SESSION['csrf_token'] ?? null;\n   $request_csrf_token = isset($_POST['csrf_token']) ? $_POST['csrf_token'] : (isset($_SERVER['HTTP_X_CSRF_TOKEN']) ? $_SERVER['HTTP_X_CSRF_TOKEN'] : null);\n\n   if ($csrf_token === $request_csrf_token) {\n       // Token is valid, continue processing\n   } else {\n       http_response_code(403);\n       echo "CSRF token mismatch. Please try again.";\n       exit;\n   }