class SessionHandler {\n    private $prefix;\n\n    public function __construct($appIdentifier) {\n        // Set a unique session name based on the app identifier\n        session_name('session_' . md5($appIdentifier));\n        \n        // Start the session if it's not already started\n        if (session_status() == PHP_SESSION_NONE) {\n            session_start();\n        }\n        \n        // Define a prefix for all session variables specific to this application\n        $this->prefix = 'app_' . md5($appIdentifier) . '_';\n    }\n\n    public function set($key, $value) {\n        $_SESSION[$this->prefix . $key] = $value;\n    }\n\n    public function get($key) {\n        return isset($_SESSION[$this->prefix . $key]) ? $_SESSION[$this->prefix . $key] : null;\n    }\n\n    // other methods like unset, check if set, etc.\n}