<?php\n\nrequire 'vendor/autoload.php';\n\nuse Respect\Validation\Validator as v;\n\n$input = "<b>Example</b>";\n\ntry {\n    // Basic string length validation\n    v::stringType()->length(1, 100)->validate($input);\n\n    // Strip HTML tags and validate again\n    $cleanInput = strip_tags($input);\n    v::stringType()->length(1, 100)->validate($cleanInput);\n\n    echo "Validation passed!";\n} catch (\Respect\Validation\Exceptions\NestedValidationExceptionInterface $exception) {\n    echo $exception->getMessage();\n}