<?php\n// Assuming $wordContent holds the content from the Word document pasted into the textbox.\n$wordContent = $_POST['word_document_content'];\n\n// Convert special HTML characters to HTML entities and ensure UTF-8 encoding is preserved.\n$safeHtmlContent = htmlspecialchars($wordContent, ENT_QUOTES | ENT_SUBSTITUTE, 'UTF-8');\n\n// Save $safeHtmlContent to your database or use as needed.\necho $safeHtmlContent;\n?>