<?php\nrequire_once 'path/to/htmlpurifier/library/HTMLPurifier.auto.php';\n\n// Assuming $wordContent holds the content from the Word document pasted into the textbox.\n$wordContent = $_POST['word_document_content'];\n\n$config = HTMLPurifier_Config::createDefault();\n$purifier = new HTMLPurifier($config);\n\n$safeHtmlContent = htmlspecialchars($purifier->purify($wordContent), ENT_QUOTES | ENT_SUBSTITUTE, 'UTF-8');\n\n// Save $safeHtmlContent to your database or use as needed.\necho $safeHtmlContent;\n?>