<?php\n   $settingsPath = 'config.ini';\n   \n   // Read settings\n   $iniArray = parse_ini_file($settingsPath, true);\n\n   // Example modification: Set display_num to false\n   $iniArray['general']['display_num'] = 'Off';\n\n   // Convert back to INI string and save\n   $newIniContent = '';\n   foreach ($iniArray as $section => $keyValues) {\n       $newIniContent .= "[$section]\n";\n       foreach ($keyValues as $key => $value) {\n           $newIniContent .= "$key = \"$value\"\n";\n       }\n       $newIniContent .= "\n";\n   }\n\n   file_put_contents($settingsPath, $newIniContent);\n   ?>