function foo1($a,$b) { return $a/$b; }\n\nclass Bar {\n    static function foo2($a,$b) { return $a/$b; }\n\n    public function UseReferences() {\n        // WORKS FINE:\n        $fn = 'foo1';\n        print $fn(1,1);\n\n        // WORKS FINE:\n        print self::foo2(2,1);\n        print Bar::foo2(3,1);\n\n        // THIS WILL NOW WORK:\n        $fn = [self::class, 'foo2'];\n        print $fn(4,1);\n\n        // ALTERNATIVELY, USING THE CLASS NAME DIRECTLY:\n        $fn = ['Bar', 'foo2'];\n        print $fn(5,1);\n    }\n}\n\n$x = new Bar();\n$x->UseReferences();