// Set default timezone for timezone-aware operations\ndate_default_timezone_set('UTC');\n\n// Fetch the datetime from database (assuming it's stored in UTC)\n$dbDateTime = '2023-10-05 15:30:00'; // Example date fetched from DB\n\n// Convert to user's timezone\n$userTimezone = new DateTimeZone('America/Los_Angeles');\n$dateTimeUTC = new DateTime($dbDateTime, new DateTimeZone('UTC'));\n$dateTimeUserTZ = $dateTimeUTC->setTimezone($userTimezone);\n\necho "User Time: " . $dateTimeUserTZ->format('Y-m-d H:i:s'); // Output in user's timezone