$dom = new DOMDocument();\n$dom->load('yourfile.xml'); // Load your XML file\n\n$foos = $dom->getElementsByTagName('foo');\n\nforeach ($foos as $foo) {\n    // Accessing <bar> node value\n    $bars = $foo->getElementsByTagName('bar');\n    if ($bars->length > 0) {\n        echo "Bar: " . $bars->item(0)->nodeValue . "\n";\n    }\n\n    // Accessing <pub> node value\n    $pubs = $foo->getElementsByTagName('pub');\n    if ($pubs->length > 0) {\n        echo "Pub: " . $pubs->item(0)->nodeValue . "\n";\n    }\n    \n    // You can also loop through all child nodes of a specific type or name\n    foreach ($foo->childNodes as $node) {\n        if ($node instanceof DOMElement && in_array($node->nodeName, ['bar', 'pub'])) {\n            echo ucfirst($node->nodeName) . ": " . $node->nodeValue . "\n";\n        }\n    }\n}