$html = '<div>This is some <b>text</b></div>'; // Assuming the </b> tag was added for completion in this example\n\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress warnings about malformed HTML\n$dom->loadHTML($html);\nlibxml_clear_errors();\n\n// You can manipulate the DOM here, for example:\n$root = $dom->getElementsByTagName('div')->item(0);\n$bTag = $dom->createElement('b', 'more text');\n$root->appendChild($bTag);\n\necho $dom->saveHTML();