define('DEFAULT_ENCRYPTION_KEY', 'asdHRMfjkahguglw84tlrogl9y8kamaFDaufasds');\n\nclass Encryption {\n    private $mode = 'aes-128-cbc';\n    private $key;\n\n    function __construct($key = DEFAULT_ENCRYPTION_KEY) {\n        // Use only the first 16 bytes for AES-128\n        $this->key = substr(hash('sha256', $key, true), 0, 16);\n    }\n\n    public function encrypt($data): string {\n        $ivSize = openssl_cipher_iv_length($this->mode);\n        $iv = openssl_random_pseudo_bytes($ivSize);\n        $encryptedData = openssl_encrypt($data, $this->mode, $this->key, OPENSSL_RAW_DATA, $iv);\n        return base64_encode($iv . $encryptedData); // Include IV at the beginning\n    }\n\n    public function decrypt($data): string {\n        $decodedData = base64_decode($data);\n        $ivSize = openssl_cipher_iv_length($this->mode);\n        $iv = substr($decodedData, 0, $ivSize);\n        $encryptedData = substr($decodedData, $ivSize);\n        return openssl_decrypt($encryptedData, $this->mode, $this->key, OPENSSL_RAW_DATA, $iv);\n    }\n}\n\n$crypt1 = new Encryption();\n$enc = $crypt1->encrypt('hello world');\n\n$crypt2 = new Encryption();\n$dec = $crypt2->decrypt($enc);\n\necho $dec;