<?php\nsession_start();\n\n// Secret key for hashing\n$secretKey = 'your_secret_key';\n\nfunction generateSecureId($id) {\n    global $secretKey;\n    return hash_hmac('sha256', $id, $secretKey);\n}\n\nfunction verifySecureId($id, $secureId) {\n    global $secretKey;\n    return hash_equals(hash_hmac('sha256', $id, $secretKey), $secureId);\n}\n\n// Generate links with secure ID\n$bookIds = [1, 2, 4];\nforeach ($bookIds as $id) {\n    echo "<a href='books.php?id={$id}&secure_id=" . generateSecureId($id) . "'>Book {$id}</a><br>";\n}\n\n// In books.php\nif (isset($_GET['id']) && isset($_GET['secure_id'])) {\n    $bookId = $_GET['id'];\n    $secureId = $_GET['secure_id'];\n\n    if (!verifySecureId($bookId, $secureId)) {\n        die("Unauthorized access");\n    }\n\n    // Proceed with database query for the book\n}\n?>