class Search extends CI_Controller {\n    public function index() {\n        // Check if the keyword is posted\n        if ($this->input->post('keyword')) {\n            $keyword = url_title($this->input->post('keyword'), 'dash', TRUE);\n            redirect("search/view/{$keyword}");\n        } else {\n            // If no keyword, display a default view or error message\n            echo "Please enter a search term.";\n        }\n    }\n\n    public function view($keyword) {\n        // Decode the URI segment to get the actual search keyword\n        $actual_keyword = str_replace('-', ' ', urldecode($keyword));\n\n        // Perform search logic here using $actual_keyword\n        // e.g., querying database, fetching results, etc.\n\n        echo "Searching for: " . htmlspecialchars($actual_keyword);\n    }\n}