<?php\n// Storing user input (raw) in the database\n$userInput = $_POST['user_input'];\n$dbConnection->query("INSERT INTO users (input) VALUES ('$userInput')");\n\n// Retrieving and displaying the user input, HTML-encoded\n$storedInput = $dbConnection->query("SELECT input FROM users WHERE id = 1")->fetch_assoc()['input'];\necho htmlspecialchars($storedInput, ENT_QUOTES, 'UTF-8');\n?>