$arrayOne = [1, 2, 3, 4, 5]; // Example array\n$index = 0;\n\nwhile ($index < count($arrayOne)) {\n    $value = $arrayOne[$index];\n    \n    if (someConditionToRemoveElement($value)) { // Replace with your condition\n        array_splice($arrayOne, $index, 1); // Remove the current element\n    } else {\n        doFunction($value);\n        $index++;\n    }\n}\n\nfunction someConditionToRemoveElement($value) {\n    // Define your condition here to determine if an element should be removed\n    return $value % 2 === 0; // Example: remove even numbers\n}\n\nfunction doFunction($value) {\n    // Your function logic here\n    echo $value . "\n";\n}