<?php\nif (isset($_SERVER['REMOTE_USER'])) {\n    $username = $_SERVER['REMOTE_USER'];\n    \n    // If the username includes a domain, you can split it:\n    list($domain, $user) = explode('\\', $username);\n    \n    echo "Domain: " . htmlspecialchars($domain) . "<br>";\n    echo "Username: " . htmlspecialchars($user) . "<br>";\n} else {\n    echo "Unable to retrieve the user's AD username.";\n}\n?>