<?php\n\n// Assuming $pdo is your PDO connection\n\nfunction saveImagePath($userId, $path) {\n    $stmt = $pdo->prepare("INSERT INTO user_images (user_id, image_path) VALUES (?, ?)");\n    return $stmt->execute([$userId, $path]);\n}\n\nfunction getImagePath($userId) {\n    $stmt = $pdo->prepare("SELECT image_path FROM user_images WHERE user_id = ?");\n    $stmt->execute([$userId]);\n    return $stmt->fetchColumn();\n}\n\n// Usage example:\n$userId = 1;\n$imagePath = '/images/profile/user1.jpg';\nsaveImagePath($userId, $imagePath);\n\n$fetchedPath = getImagePath($userId);\necho "<img src='{$fetchedPath}' alt='User Profile Image'>";\n?>