<?php\n\n// Define the base URL path to your script as per .htaccess config or manually\n$baseURL = '/api/';\n\n$uri = parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH);\n$uri = substr($uri, strlen($baseURL)); // Remove the base URL part\n$uri = explode('/', trim($uri, '/'));\n$method = $_SERVER['REQUEST_METHOD'];\n\n// Handle GET request\nif ($method == 'GET') {\n    if (isset($uri[0]) && $uri[0] == "users") {  // Check if we want to get users details\n        $id = null;\n        if (isset($uri[1])) { // Check if there's an ID specified\n            $id = intval($uri[1]);\n        }\n        \n        // Here you would normally fetch data from a database\n        // and return it in JSON format\n        header('Content-Type: application/json');\n        echo json_encode(["message" => "GET request for users", "id" => $id]);\n    } else {\n        // Handle 404 Not Found\n        http_response_code(404);\n        echojson_encode(["error" => "Endpoint not found"]);\n    }\n}\n\n// Handle POST request\nif ($method == 'POST') {\n    if (isset($uri[0]) && $uri[0] == "users") {  // Assume adding a new user\n        // Normally you would decode the JSON input and then insert into database\n        $input = json_decode(file_get_contents('php://input'), true);\n        \n        header('Content-Type: application/json');\n        echo json_encode(["message" => "POST request for users", "data_received" => $input]);\n    } else {\n        // Handle 404 Not Found\n        http_response_code(404);\n        echo json_encode(["error" => "Endpoint not found"]);\n    }\n}\n\n// Other methods like PUT, DELETE can be handled similarly\n\n?>