<?php\n    $items = array(12, 17, 27, 26, 5); // an array of IDs in the desired order\n\n    $cases = [];\n    foreach ($items as $priority => $id) {\n        $cases[] = "WHEN `ID` = {$id} THEN {$priority}";\n    }\n\n    $caseString = implode(' ', $cases);\n\n    $sql = "UPDATE `table`\n            SET `priorityLevel` = CASE {$caseString}\n            END\n            WHERE `ID` IN (" . implode(', ', $items) . ")";\n\n    // Execute the query\n    $database->query($sql);\n?>