<?php\n$serverName = "your_server";\n$dbOptions = array(\n    "Database" => "your_database",\n    "Uid" => "your_username",\n    "PWD" => "your_password"\n);\n\n$conn = sqlsrv_connect($serverName, $dbOptions);\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$sql = "INSERT INTO your_table (column1, column2) VALUES ('value1', 'value2'); SELECT SCOPE_IDENTITY() AS ins_id;";\n\n$stmt = sqlsrv_query($conn, $sql);\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$result = sqlsrv_fetch_array($stmt, SQLSRV_FETCH_ASSOC);\necho "Inserted ID: " . $result['ins_id'];\n\nsqlsrv_free_stmt($stmt);\nsqlsrv_close($conn);\n?>