<?php\n// Temporary hack to log exits\nfunction my_exit($status = 0, $message = 'Script exited') {\n    error_log("Exit called with message: '$message' and status: '$status'");\n    exit($status);\n}\n\nfunction my_die($message = '', $status = 0) {\n    error_log("Die called with message: '$message' and status: '$status'");\n    die($message, $status);\n}\n\n// Usage\nmy_exit('Testing');\n?>