// In bootstrap.php, add or modify the routes section\n\nRoute::set('default', '(<controller>(/<action>(/<id>)))')\n    ->defaults(array(\n        'controller' => 'articles',\n        'action'     => 'view'\n));\n\n// Add a custom route to handle requests like /bures/oceanfront\nRoute::set('article_view', '(.*)',\n    array(0 => '(?!articles|admin).*')) // Ensure we don't clash with existing controllers like 'articles' or 'admin'\n    ->defaults(array(\n        'controller' => 'articles',\n        'action'     => 'view'\n));