<?php // includes/PluginManager.php\ninclude_once 'HookHandler.php';\n\nclass PluginManager {\n    private $hookHandler;\n\n    public function __construct(HookHandler $hookHandler) {\n        $this->hookHandler = $hookHandler;\n    }\n\n    public function loadPlugins($pluginDir) {\n        if (is_dir($pluginDir)) {\n            foreach (scandir($pluginDir) as $file) {\n                if ($file != '.' && $file != '..') {\n                    include_once $pluginDir . '/' . $file . '/' . ucfirst($file) . '.php';\n                }\n            }\n        }\n    }\n}