$sql = "SELECT HOUR(timestamp_column) AS hour, COUNT(*) AS visits \n        FROM visits \n        WHERE timestamp_column >= NOW() - INTERVAL 24 HOUR \n        GROUP BY HOUR(timestamp_column) \n        ORDER BY hour;";\n\n$res = mysqli_query($conn, $sql); // Use mysqli or PDO\n$visits = [];\nwhile ($row = mysqli_fetch_assoc($res)) {\n    $visits[$row['hour']] = $row['visits'];\n}\n\nfor ($i = 0; $i < 24; $i++) {\n    echo "Hour $i: " . (isset($visits[$i]) ? $visits[$i] : 0) . "\n";\n}