function stripClassesFromPTagsUsingDOM($html) {\n    $dom = new DOMDocument();\n    libxml_use_internal_errors(true); // Suppress errors for invalid HTML\n    $dom->loadHTML($html);\n    libxml_clear_errors();\n\n    foreach ($dom->getElementsByTagName('p') as $p) {\n        if ($p->hasAttribute('class')) {\n            $p->removeAttribute('class');\n        }\n    }\n\n    return $dom->saveHTML();\n}\n\n// Example usage\n$html = '<p class="example">This is a paragraph.</p>';\n$cleanHtml = stripClassesFromPTagsUsingDOM($html);\necho $cleanHtml;