case Zend_Auth_Result::FAILURE_CREDENTIAL_INVALID:\n    $this->_messages->addMessage("That wasn't the right password.");\n    // Redirect to avoid retrieving the message in the current request\n    $this->_helper->Redirector('login', 'auth');\n    break;\n\ncase Zend_Auth_Result::SUCCESS:\n    $this->_messages->addMessage('Logged you in successfully. Welcome back!');\n    $this->_helper->Redirector('index', 'home'); // or wherever applicable\n    break;