// Fetch data from the database\n$result = $connector->query('SELECT ID, name, parentid FROM categories');\n\n// Populate an associative array with category data\n$categories = [];\nwhile ($row = $connector->fetchArray($result)) {\n    $categories[$row['ID']] = ['name' => $row['name'], 'children' => [], 'parentid' => $row['parentid']];\n}\n\n// Build the tree structure\nfunction buildTree(&$elements, $parentId = 0) {\n    static $branch = [];\n    foreach ($elements as $element) {\n        if ($element['parentid'] == $parentId) {\n            $children = buildTree($elements, $element['ID']);\n            if ($children) {\n                $element['children'] = $children;\n            }\n            $branch[$element['ID']] = $element;\n        }\n    }\n    return $branch;\n}\n\n$tree = buildTree($categories);\n\n// Print the tree structure\nfunction printTree(array $elements, $prefix = '') {\n    foreach ($elements as $element) {\n        echo $prefix . $element['name']  . "<br>";\n        if (!empty($element['children'])) {\n            printTree($element['children'], $prefix . '    -> ');\n        }\n    }\n}\n\nprintTree($tree);