<?php\n// Define the command and arguments as an array to prevent shell injection\n$command = '/usr/bin/python3';\n$args = ['path/to/your_script.py', '--option', 'value'];\n\n// Prepare the descriptor spec for proc_open\n$descriptorspec = [\n    0 => ["pipe", "r"],  // STDIN is a pipe that the child will read from\n    1 => ["pipe", "w"],  // STDOUT is a pipe that the child will write to\n    2 => ["pipe", "w"]   // STDERR is a pipe that the child will write to\n];\n\n// Execute the process\n$process = proc_open($command, array_merge([$command], $args), $pipes, null, null);\n\nif (is_resource($process)) {\n    // Optionally, write input to STDIN here:\n    fclose($pipes[0]);\n\n    $output = stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n\n    $errors = stream_get_contents($pipes[2]);\n    fclose($pipes[2]);\n\n    // It is important that you close any pipes before calling\n    // proc_close in order to avoid a deadlock\n    $return_value = proc_close($process);\n\n    echo "Output:\n";\n    echo $output;\n    echo "\nErrors:\n";\n    echo $errors;\n}\n?>